import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-4324 Regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4324.md","filePath":"vacode/3.2-4324.md"}'),n={name:"vacode/3.2-4324.md"},r=i('<h1 id="§-3-2-4324-regulations" tabindex="-1">§ 3.2-4324 Regulations. <a class="header-anchor" href="#§-3-2-4324-regulations" aria-label="Permalink to &quot;§ 3.2-4324 Regulations.&quot;">​</a></h1><p>The Board may adopt regulations relating to the handling of grain in the Commonwealth, including:</p><ol><li><p>The weighing devices, approved under the Virginia Weights and Measures Law (§ <a href="http://law.lis.virginia.gov/vacode/3.2-5600/" target="_blank" rel="noreferrer">3.2-5600</a> et seq.) and the procedures employed to give accurate weights.</p></li><li><p>The grading equipment that is acceptable in administering the United States Grain Standards Act and the use of grading equipment to be used in determining the value of grain. Such use of such equipment shall be pursuant to procedures employed by inspectors licensed under the United States Grain Standards Act.</p></li><li><p>Samples of lots graded by other than a Virginia licensed inspector and the lot discounted shall be identity preserved for 24 hours.</p></li><li><p>The keeping of records in accordance with good business practices.</p></li></ol><p>Code 1950, § 3-269.3; 1966, c. 433, § 3.1-348.3; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),o=[r];function s(d,l,c,p,h,g){return t(),a("div",null,o)}const f=e(n,[["render",s]]);export{_ as __pageData,f as default};
