import{_ as e,o as a,c as t,O as s}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-4410 Measures to eradicate and control bee diseases; appeal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4410.md","filePath":"vacode/3.2-4410.md"}'),o={name:"vacode/3.2-4410.md"},r=s('<h1 id="§-3-2-4410-measures-to-eradicate-and-control-bee-diseases-appeal" tabindex="-1">§ 3.2-4410 Measures to eradicate and control bee diseases; appeal. <a class="header-anchor" href="#§-3-2-4410-measures-to-eradicate-and-control-bee-diseases-appeal" aria-label="Permalink to &quot;§ 3.2-4410 Measures to eradicate and control bee diseases; appeal.&quot;">​</a></h1><p>A. The State Apiarist shall examine or inspect the bees in the Commonwealth whenever they are suspected of being infected with bee diseases and, on request, shall inspect bees to be sold or to be transported interstate.</p><p>B. If bees are found to be infected with bee diseases, the State Apiarist shall take suitable measures to eradicate or control such diseases.</p><p>C. If the owner of such diseased bees fails to take such steps as may be prescribed by the State Apiarist to eradicate or control the disease, the State Apiarist shall destroy or treat the bees, hives, and honey.</p><p>D. The State Apiarist may prohibit the removal of bees, honey, wax, combs, hives, or other used beekeeping equipment from any place where bees are known to be infected with bee diseases, until he issues a certificate of health for such place.</p><p>E. Within 10 days from the receipt of an order from the State Apiarist to destroy or treat his diseased bees, hives, honey, or appliances, any owner of diseased bees may file a written appeal with the Commissioner. Upon timely receipt of a written appeal under this section, the Commissioner shall act upon the appeal in accordance with the provisions of the Administrative Process Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4000/" target="_blank" rel="noreferrer">2.2-4000</a> et seq.).</p><p>Code 1950, §§ 3-487, 3-490, 3-491, 3-493; 1966, c. 702, §§ 3.1-592, 3.1-595, 3.1-596, 3.1-598; 1972, c. 499, §§ 3.1-610.5, 3.1-610.6, 3.1-610.11; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',7),i=[r];function n(c,d,p,h,l,f){return a(),t("div",null,i)}const _=e(o,[["render",n]]);export{m as __pageData,_ as default};
