import{_ as t,o,c as i,z as e,a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-4411 Abandoned apiaries.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4411.md","filePath":"vacode/3.2-4411.md"}'),n={name:"vacode/3.2-4411.md"},r=e("h1",{id:"§-3-2-4411-abandoned-apiaries",tabindex:"-1"},[a("§ 3.2-4411 Abandoned apiaries. "),e("a",{class:"header-anchor",href:"#§-3-2-4411-abandoned-apiaries","aria-label":'Permalink to "§ 3.2-4411 Abandoned apiaries."'},"​")],-1),s=e("p",null,"The State Apiarist may deem an apiary to be abandoned if: (i) the bees and hives show evidence of a period of neglect exceeding one year; and (ii) the owner of the apiary has not been identified through a reasonable search of available records. If the State Apiarist deems an apiary to be abandoned, he shall certify his findings in a declaration of abandonment to the treasurer of the locality where the apiary is located. The treasurer shall give notice of such certification to the last known owner of the apiary and the owner of the land upon which the apiary is located by personal service, by posting at last known residence, or by publication. If after 60 days, the owner or landowner has not laid claim to the apiary, the treasurer may hold a sheriff's sale, issue a treasurer's deed to the successful bidder, and deposit any proceeds into the general fund of the locality. If disposition is not made within 90 days of the date of the declaration of abandonment, the State Apiarist may take possession of the apiary and destroy the related bees, hives, and equipment.",-1),d=e("p",null,[a("1972, c. 499, § 3.1-610.18; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),a(".")],-1),h=[r,s,d];function c(l,p,f,b,y,_){return o(),i("div",null,h)}const g=t(n,[["render",c]]);export{m as __pageData,g as default};
