import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-4505 Enforcement of chapter.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4505.md","filePath":"vacode/3.2-4505.md"}'),a={name:"vacode/3.2-4505.md"},n=t('<h1 id="§-3-2-4505-enforcement-of-chapter" tabindex="-1">§ 3.2-4505 Enforcement of chapter. <a class="header-anchor" href="#§-3-2-4505-enforcement-of-chapter" aria-label="Permalink to &quot;§ 3.2-4505 Enforcement of chapter.&quot;">​</a></h1><p>The Commissioner may:</p><ol><li><p>Enter and inspect all places within the Commonwealth where apples are produced, packed or stored for sale, shipped, delivered for shipment, offered or exposed for sale, or sold, and to inspect all apples, containers and equipment found in any such places.</p></li><li><p>Institute injunction proceedings for violations of any provision of this chapter or regulation adopted hereunder in any circuit court in any county or city of the Commonwealth where apples may be found improperly marked in violation of any provision of this chapter, either through the attorney for the Commonwealth or otherwise.</p></li><li><p>Prohibit in writing the movement in intrastate, interstate or foreign commerce of any apples found improperly marked in violation of any provision of this chapter or regulation adopted hereunder until such apples are properly marked and released in writing by the Commissioner.</p></li></ol><p>Code 1950, § 3-512.6; 1954, c. 697; 1966, c. 702, § 3.1-616; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),i=[n];function p(c,s,l,h,d,f){return o(),r("div",null,i)}const u=e(a,[["render",p]]);export{_ as __pageData,u as default};
