import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-4754 License required; application for license or renewal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4754.md","filePath":"vacode/3.2-4754.md"}'),o={name:"vacode/3.2-4754.md"},r=n('<h1 id="§-3-2-4754-license-required-application-for-license-or-renewal" tabindex="-1">§ 3.2-4754 License required; application for license or renewal. <a class="header-anchor" href="#§-3-2-4754-license-required-application-for-license-or-renewal" aria-label="Permalink to &quot;§ 3.2-4754 License required; application for license or renewal.&quot;">​</a></h1><p>A. No person shall act as a grain dealer without first having obtained a license.</p><p>B. Every grain dealer proposing to transact business within the Commonwealth shall annually on or before January 1, file a written application for a license or for the renewal of a license with the Commissioner. The application shall be on a form furnished by the Commissioner and shall contain the following information and such other relevant information as the Commissioner shall require:</p><ol><li><p>The name and address of the applicant and that of its local agent or agents, if any, and the location of its principal place of business within the Commonwealth;</p></li><li><p>The kinds of grain the applicant proposes to handle; and</p></li><li><p>The type of grain business proposed to be conducted.</p></li></ol><p>1972, c. 296, §§ 3.1-722.17, 3.1-722.18; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),t=[r];function l(s,c,p,h,d,f){return a(),i("div",null,t)}const u=e(o,[["render",l]]);export{m as __pageData,u as default};
