import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-4764 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4764.md","filePath":"vacode/3.2-4764.md"}'),r={name:"vacode/3.2-4764.md"},a=n('<h1 id="§-3-2-4764-definitions" tabindex="-1">§ 3.2-4764 Definitions. <a class="header-anchor" href="#§-3-2-4764-definitions" aria-label="Permalink to &quot;§ 3.2-4764 Definitions.&quot;">​</a></h1><p>As used in this article, unless the context requires a different meaning:</p><p>&quot;Cotton gin&quot; means a facility where cotton seed and cotton lint are produced from raw cotton.</p><p>&quot;Cotton handler&quot; means any person doing business as a cotton gin, cotton merchant, or cotton warehouse.</p><p>&quot;Cotton merchant&quot; means any person who buys cotton from a producer for the purpose of resale, or acts as a broker or agent for a producer in arranging the sale of cotton. The term does not include a person that buys cotton for his own use.</p><p>&quot;Cotton warehouse&quot; means any enclosure in which producer-owned cotton is stored or held for longer than 48 hours.</p><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0584" target="_blank" rel="noreferrer">584</a>, § 3.1-722.29; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',7),i=[a];function s(c,d,p,h,l,u){return o(),t("div",null,i)}const m=e(r,[["render",s]]);export{_ as __pageData,m as default};
