import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-4775 Offenses and punishment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4775.md","filePath":"vacode/3.2-4775.md"}'),i={name:"vacode/3.2-4775.md"},s=n('<h1 id="§-3-2-4775-offenses-and-punishment" tabindex="-1">§ 3.2-4775 Offenses and punishment. <a class="header-anchor" href="#§-3-2-4775-offenses-and-punishment" aria-label="Permalink to &quot;§ 3.2-4775 Offenses and punishment.&quot;">​</a></h1><p>Any cotton handler is guilty of a Class 1 misdemeanor if he:</p><ol><li><p>Markets, obligates for sale, or otherwise disposes of producer-owned cotton without the written consent of the producer;</p></li><li><p>Conduct business without the license required by this article;</p></li><li><p>Imposes false charges for the handling of cotton;</p></li><li><p>Fails to account promptly, accurately, fully, and properly and to make settlement;</p></li><li><p>Intentionally makes any false statement with regard to grade, condition, markings, quality, or quantity of cotton received, ginned, packed, shipped, or otherwise handled, to the consignor of cotton with respect to the consignor&#39;s cotton, or to the Commissioner;</p></li><li><p>Fails to maintain records as required by this article;</p></li><li><p>In any instance in which the cotton handler offers to buy the consignor&#39;s cotton, fails to disclose to the consignor that the person making the offer is composed substantially of the same persons, as stockholders, members, or otherwise, who compose the cotton handler business;</p></li><li><p>Refuses the Commissioner the right of entry authorized by this article;</p></li><li><p>Knowingly provides false information on an application for license;</p></li><li><p>Fails to give reasonable written notice of any change in the style, name, or personnel of the cotton handler to the Commissioner or to the surety on the bond required by this article; or</p></li><li><p>Violates any provision of this article or regulation adopted hereunder.</p></li></ol><p>2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0584" target="_blank" rel="noreferrer">584</a>, § 3.1-722.43; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),a=[s];function r(l,c,h,p,d,f){return t(),o("div",null,a)}const _=e(i,[["render",r]]);export{u as __pageData,_ as default};
