import{_ as e,o as s,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-4814 Disposition of fees, assessments, and penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-4814.md","filePath":"vacode/3.2-4814.md"}'),i={name:"vacode/3.2-4814.md"},n=t('<h1 id="§-3-2-4814-disposition-of-fees-assessments-and-penalties" tabindex="-1">§ 3.2-4814 Disposition of fees, assessments, and penalties. <a class="header-anchor" href="#§-3-2-4814-disposition-of-fees-assessments-and-penalties" aria-label="Permalink to &quot;§ 3.2-4814 Disposition of fees, assessments, and penalties.&quot;">​</a></h1><p>All licensing, registration and inspection fees, assessments and penalties under this chapter received by the Commissioner shall be paid into the Feed, Lime, Fertilizer, and Animal Remedies Fund, established in § <a href="http://law.lis.virginia.gov/vacode/3.2-3617/" target="_blank" rel="noreferrer">3.2-3617</a>. The fund shall be used in carrying out the purpose and provisions of this chapter, to include inspection, sampling and other expenses; except that seven cents ($0.07) per ton of commercial feed per license year of inspection fees received by the Commissioner shall be transferred to the Virginia Agricultural Foundation Fund pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-2905/" target="_blank" rel="noreferrer">3.2-2905</a>.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0743" target="_blank" rel="noreferrer">743</a>, § 3.1-828.17; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),r=[n];function o(l,d,p,c,f,h){return s(),a("div",null,r)}const g=e(i,[["render",o]]);export{m as __pageData,g as default};
