import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-505 Emergency services.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-505.md","filePath":"vacode/3.2-505.md"}'),o={name:"vacode/3.2-505.md"},i=t('<h1 id="§-3-2-505-emergency-services" tabindex="-1">§ 3.2-505 Emergency services. <a class="header-anchor" href="#§-3-2-505-emergency-services" aria-label="Permalink to &quot;§ 3.2-505 Emergency services.&quot;">​</a></h1><p>A. The Commissioner may develop and initiate programs of general relief to farmers affected by major disasters and to expend moneys from the Farmers Major Disaster Fund in order to implement such programs. Programs created pursuant hereto shall include but not be limited to the following:</p><ol><li><p>Programs to assist farmers in their feed needs including the supplying of feed at cost.</p></li><li><p>Programs to provide supplemental manpower to those state and federal agencies involved in relief efforts to aid farmers.</p></li></ol><p>B. Any locality may develop and initiate a grant program to supply emergency financial assistance to farmers in the locality to offset a portion of any operating losses resulting from a major disaster as declared by the Governor pursuant to § <a href="http://law.lis.virginia.gov/vacode/44-146.17/" target="_blank" rel="noreferrer">44-146.17</a>.</p><p>1978, c. 837, § 3.1-22.19; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0016" target="_blank" rel="noreferrer">16</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),s=[i];function n(l,c,d,p,m,f){return r(),a("div",null,s)}const h=e(o,[["render",n]]);export{_ as __pageData,h as default};
