import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 3.2-5122 Adulterated food.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5122.md","filePath":"vacode/3.2-5122.md"}'),n={name:"vacode/3.2-5122.md"},i=o('<h1 id="§-3-2-5122-adulterated-food" tabindex="-1">§ 3.2-5122 Adulterated food. <a class="header-anchor" href="#§-3-2-5122-adulterated-food" aria-label="Permalink to &quot;§ 3.2-5122 Adulterated food.&quot;">​</a></h1><p>A food shall be deemed to be adulterated if:</p><ol><li><p>It bears or contains any poisonous or deleterious substance that may render it injurious to health; but if the substance is not an added substance the food shall not be considered adulterated under this subdivision if the quantity of the substance in the food does not ordinarily render it injurious to health;</p></li><li><p>It bears or contains any added poisonous or added deleterious substance that is unsafe within the meaning of § <a href="http://law.lis.virginia.gov/vacode/3.2-5125/" target="_blank" rel="noreferrer">3.2-5125</a>;</p></li><li><p>It consists in whole or in part of a diseased, contaminated, filthy, putrid, or decomposed substance, or if it is otherwise unfit for food;</p></li><li><p>It has been produced, prepared, packed, or held under unsanitary conditions whereby it may have become contaminated with filth, or whereby it may have been rendered diseased, unwholesome, or injurious to health;</p></li><li><p>It is the product of a diseased animal, an animal that has died otherwise than by slaughter, or an animal that has been fed upon the uncooked offal from a slaughterhouse;</p></li><li><p>Its container is composed, in whole or in part, of any poisonous or deleterious substance that may render the contents injurious to health;</p></li><li><p>Any valuable constituent has been, in whole or in part, omitted or abstracted;</p></li><li><p>Any substance has been substituted in whole or in part for a valuable constituent;</p></li><li><p>Damage or inferiority has been concealed in any manner;</p></li><li><p>Any substance has been added or mixed or packed with the food so as to increase its bulk or weight, or reduce its quality or strength, or make it appear better or of greater value than it is;</p></li><li><p>It is confectionery and it bears or contains any alcohol or nonnutritive article or substance, except harmless coloring, harmless flavoring, harmless resinous glaze not in excess of four-tenths of one percent, harmless natural gum, and pectin. In addition, any confectionery that: (i) contains five percent or less by volume of alcohol or; (ii) any chewing gum that contains harmless nonnutritive masticatory substances, shall not be deemed adulterated; or</p></li><li><p>It bears or contains a coal-tar color other than one from a batch that has been certified by the U.S. Department of Health and Human Services.</p></li></ol><p>Code 1950, § 3-315; 1966, c. 702, § 3.1-395; 1988, c. 110; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),r=[i];function s(d,l,h,c,u,p){return t(),a("div",null,r)}const m=e(n,[["render",s]]);export{b as __pageData,m as default};
