import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-5139 Disposition of proceeds from sale of such goods.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5139.md","filePath":"vacode/3.2-5139.md"}'),t={name:"vacode/3.2-5139.md"},s=a('<h1 id="§-3-2-5139-disposition-of-proceeds-from-sale-of-such-goods" tabindex="-1">§ 3.2-5139 Disposition of proceeds from sale of such goods. <a class="header-anchor" href="#§-3-2-5139-disposition-of-proceeds-from-sale-of-such-goods" aria-label="Permalink to &quot;§ 3.2-5139 Disposition of proceeds from sale of such goods.&quot;">​</a></h1><p>The proceeds arising from any sale of seized property or goods shall be disposed of in accordance with § <a href="http://law.lis.virginia.gov/vacode/19.2-386.14/" target="_blank" rel="noreferrer">19.2-386.14</a>. If the owner or party claiming the property or goods can produce and prove a written guaranty of purity, signed by the wholesaler, jobber, manufacturers, or other party residing within the Commonwealth from whom the articles were purchased, then the proceeds of the sale, over and above the costs of seizure, forfeiture and sale, shall be paid to the owner or claimant to reimburse him, to the extent of such surplus, for his actual loss resulting from the seizure and forfeiture as shown by the invoice.</p><p>Code 1950, § 3-334; 1966, c. 702, § 3.1-413; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',3),i=[s];function n(c,l,f,h,d,p){return o(),r("div",null,i)}const u=e(t,[["render",n]]);export{g as __pageData,u as default};
