import{_ as e,o as i,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-5408 Prohibitions against unauthorized use of any official marks.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5408.md","filePath":"vacode/3.2-5408.md"}'),r={name:"vacode/3.2-5408.md"},t=o('<h1 id="§-3-2-5408-prohibitions-against-unauthorized-use-of-any-official-marks" tabindex="-1">§ 3.2-5408 Prohibitions against unauthorized use of any official marks. <a class="header-anchor" href="#§-3-2-5408-prohibitions-against-unauthorized-use-of-any-official-marks" aria-label="Permalink to &quot;§ 3.2-5408 Prohibitions against unauthorized use of any official marks.&quot;">​</a></h1><p>A. No brand manufacturer, printer, or other person shall cast, print, lithograph, or otherwise make any device containing any official mark or simulation thereof, or any label bearing any such mark or simulation, or any form of official certificate or simulation thereof, except as authorized by the Commissioner.</p><p>B. No person shall:</p><ol><li><p>Forge any official device, mark, or certificate;</p></li><li><p>Without authorization from the Commissioner, use any official device, mark, or certificate, or simulation thereof, or alter, detach, deface, or destroy any official device, mark, or certificate;</p></li><li><p>Contrary to the regulations prescribed by the Board, fail to use, or to detach, deface, or destroy any official device, mark, or certificate;</p></li><li><p>Knowingly possess, without promptly notifying the Commissioner any official device or any counterfeit, simulated, forged, or improperly altered official certificate or any device or label or any carcass of any animal (including poultry), or part or product thereof, bearing any counterfeit, simulated, forged, or improperly altered official mark;</p></li><li><p>Knowingly make any false statement in any shipper&#39;s certificate or other nonofficial or official certificate provided for in the regulations prescribed by the Board; or</p></li><li><p>Knowingly represent that any article has been inspected and passed, or exempted, under this article when, in fact, it has, respectively, not been so inspected and passed, or exempted.</p></li></ol><p>1970, c. 290, § 3.1-884.23; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),n=[t];function s(c,l,f,d,p,h){return i(),a("div",null,n)}const y=e(r,[["render",s]]);export{u as __pageData,y as default};
