import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-5509 Application; registration term; fees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5509.md","filePath":"vacode/3.2-5509.md"}'),r={name:"vacode/3.2-5509.md"},n=i('<h1 id="§-3-2-5509-application-registration-term-fees" tabindex="-1">§ 3.2-5509 Application; registration term; fees. <a class="header-anchor" href="#§-3-2-5509-application-registration-term-fees" aria-label="Permalink to &quot;§ 3.2-5509 Application; registration term; fees.&quot;">​</a></h1><p>A. Except as provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-5510/" target="_blank" rel="noreferrer">3.2-5510</a>, on and after September 1, 2010, any person who transports waste kitchen grease shall submit an application for registration to the Department not less than 30 days before such transportation. Registration shall be for a term no longer than one year. The application shall be submitted in accordance with a procedure established by the Department for this purpose. The application shall include:</p><ol><li><p>The applicant&#39;s name and address;</p></li><li><p>A description of the operations to be performed by the applicant;</p></li><li><p>The make, model, license number, and vehicle identification number of any vehicle to be used for the transportation of waste kitchen grease;</p></li><li><p>A nonrefundable application fee of $100;</p></li><li><p>A fee of $100 per vehicle used to transport waste kitchen grease; and</p></li><li><p>Proof of personal injury and property damage liability insurance in an amount not less than $1 million.</p></li></ol><p>B. The Department shall issue each registrant a unique registration number and a registration certificate.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>.</p>',5),o=[n];function s(p,l,c,h,d,f){return a(),t("div",null,o)}const u=e