import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-5510 Individual use; limitations and additional requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5510.md","filePath":"vacode/3.2-5510.md"}'),o={name:"vacode/3.2-5510.md"},n=a('<h1 id="§-3-2-5510-individual-use-limitations-and-additional-requirements" tabindex="-1">§ 3.2-5510 Individual use; limitations and additional requirements. <a class="header-anchor" href="#§-3-2-5510-individual-use-limitations-and-additional-requirements" aria-label="Permalink to &quot;§ 3.2-5510 Individual use; limitations and additional requirements.&quot;">​</a></h1><p>A. An individual who transports waste kitchen grease for his own conversion to biofuel shall not be required to register pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-5509/" target="_blank" rel="noreferrer">3.2-5509</a> provided that he:</p><ol><li><p>Transports waste kitchen grease in a container or containers with a total capacity of no more than 275 gallons on any one vehicle at any time;</p></li><li><p>Possesses or controls no more than 1,320 gallons of waste kitchen grease, biofuel feedstock derived from waste kitchen grease, or biofuel at any time, excluding biofuel contained in vehicle fuel tanks used to power the vehicle&#39;s movement; and</p></li><li><p>Does not obtain waste kitchen grease from (i) a container owned by a registered transporter of waste kitchen grease, (ii) a food establishment under contract with a registered transporter of waste kitchen grease, or (iii) a container owned by a renderer or collection center.</p></li></ol><p>B. An individual who transports waste kitchen grease to a facility for the purpose of conversion to biofuel shall not be required to register pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-5509/" target="_blank" rel="noreferrer">3.2-5509</a> provided that:</p><ol><li><p>He meets all the requirements of subsection A;</p></li><li><p>He transports waste kitchen grease to no more than one facility, other than on his own property, during a one-day period;</p></li><li><p>Such facility has a capacity to produce no more than 500 gallons per day of biofuel; and</p></li><li><p>Such facility does not possess or control more than 1,320 gallons of waste kitchen grease, biofuel feedstock derived from waste kitchen grease, or biofuel at any time, excluding biofuel contained in vehicle fuel tanks to power the vehicle&#39;s movement.</p></li></ol><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>.</p>',6),r=[n];function s(l,d,c,h,p,u){return t(),i("div",null,r)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
