import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-5515 Penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5515.md","filePath":"vacode/3.2-5515.md"}'),n={name:"vacode/3.2-5515.md"},o=r('<h1 id="§-3-2-5515-penalty" tabindex="-1">§ 3.2-5515 Penalty. <a class="header-anchor" href="#§-3-2-5515-penalty" aria-label="Permalink to &quot;§ 3.2-5515 Penalty.&quot;">​</a></h1><p>A. Except for waste kitchen grease transported and delivered pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-5510/" target="_blank" rel="noreferrer">3.2-5510</a>, on and after September 1, 2010, no person shall take possession of more than 55 gallons of waste kitchen grease from an unregistered transporter unless the recipient maintains documentation for two years, which shall be made available for inspection by the Department, of the (i) name and address of the person delivering the waste kitchen grease, (ii) date of receipt of the waste kitchen grease, (iii) delivering vehicle&#39;s license plate number and state of registration, and (iv) quantity delivered.</p><p>B. The Commissioner may assess a civil penalty of not more than $5,000 for any violation of a provision of this chapter.</p><p>C. Any person required to register with the Department pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-5509/" target="_blank" rel="noreferrer">3.2-5509</a> who fails to so register is guilty of a Class 3 misdemeanor.</p><p>2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>.</p>',5),i=[o];function s(l,p,c,d,h,f){return a(),t("div",null,i)}const g=e(n,[["render",s]]);export{m as __pageData,g as default};
