import{_ as s,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-5631 Representations as to price; signs advertising price of petroleum products.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5631.md","filePath":"vacode/3.2-5631.md"}'),o={name:"vacode/3.2-5631.md"},n=e("h1",{id:"§-3-2-5631-representations-as-to-price-signs-advertising-price-of-petroleum-products",tabindex:"-1"},[t("§ 3.2-5631 Representations as to price; signs advertising price of petroleum products. "),e("a",{class:"header-anchor",href:"#§-3-2-5631-representations-as-to-price-signs-advertising-price-of-petroleum-products","aria-label":'Permalink to "§ 3.2-5631 Representations as to price; signs advertising price of petroleum products."'},"​")],-1),i=e("p",null,'Whenever any commodity or service is sold, or is offered, exposed, or advertised for sale, by weight, measure or count, the price shall not be misrepresented, nor shall the price be represented in any manner tending to mislead or deceive an actual or prospective purchaser. Whenever an advertised, posted, or labeled price per unit of weight, measure, or count includes a fraction of a cent, all elements of the fraction shall be prominently displayed and the numerals expressing the fraction shall be immediately adjacent to, of the same general design and style as, and at least one half the height and width of, the numerals representing the whole cents. Whenever the price of petroleum products is advertised or posted at retail, the amount of any taxes may not be shown separately in such advertising or posting unless the words "plus tax" and the numerals expressing the taxes are prominently displayed in letters and numerals of the same general design and style as, and at least one half the height and width of, the numerals representing the price as specified in this section. The total price of the petroleum products so advertised or posted shall not differ from the price as shown on the pump or in any computed price charged the customer.',-1),d=e("p",null,[t("Code 1950, § 3-708.31; 1962, c. 298; 1966, c. 702, § 3.1-949; 1993, c. 604; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(".")],-1),c=[n,i,d];function p(l,h,m,u,f,g){return r(),a("div",null,c)}const y=s(o,[["render",p]]);export{_ as __pageData,y as default};
