import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-5705 Exemption from registration fee.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5705.md","filePath":"vacode/3.2-5705.md"}'),r={name:"vacode/3.2-5705.md"},a=o('<h1 id="§-3-2-5705-exemption-from-registration-fee" tabindex="-1">§ 3.2-5705 Exemption from registration fee. <a class="header-anchor" href="#§-3-2-5705-exemption-from-registration-fee" aria-label="Permalink to &quot;§ 3.2-5705 Exemption from registration fee.&quot;">​</a></h1><p>A. No service agency shall be required to pay a registration fee under the provisions of this chapter if it is:</p><ol><li><p>A business employing service technicians to install, place in service, adjust, repair, service, or recondition weights or measures that are owned or operated by the business but that are used by no other business; or</p></li><li><p>A government or political subdivision engaged in the installation, placing in service, adjusting, repairing, servicing, or reconditioning of weights or measures owned or operated by the government or political subdivision.</p></li></ol><p>B. A service technician that is employed by a business that is exempt from paying a registration fee pursuant to subsection A of this section shall not be required to pay a certification fee under the provisions of this chapter.</p><p>1992, c. 242, § 3.1-969.6; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),n=[a];function s(c,p,l,d,h,f){return i(),t("div",null,n)}const g=e(r,[["render",s]]);export{m as __pageData,g as default};
