import{_ as i,o as a,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-5810 Entries on weight certificate.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-5810.md","filePath":"vacode/3.2-5810.md"}'),n={name:"vacode/3.2-5810.md"},o=e("h1",{id:"§-3-2-5810-entries-on-weight-certificate",tabindex:"-1"},[t("§ 3.2-5810 Entries on weight certificate. "),e("a",{class:"header-anchor",href:"#§-3-2-5810-entries-on-weight-certificate","aria-label":'Permalink to "§ 3.2-5810 Entries on weight certificate."'},"​")],-1),s=e("p",null,"A licensed public weighmaster shall not enter on a weight certificate issued by him any weight values but those he has personally determined, and he shall make no entries on a weight certificate issued by some other person. A weight certificate shall be so prepared as to show clearly what weight or weights were actually determined. If the certificate form provides for the entry of gross, tare, and net weights, in any case in which only the gross, the tare, or the net weight is determined by the weighmaster he shall strike through or otherwise cancel the printed entries for the weights not determined or computed. If gross and tare weights are shown on a weight certificate and both of these were not determined on the same scale and on the day for which the certificate is dated, the weighmaster shall identify on the certificate the scale used for determining each weight and the date of each determination.",-1),h=e("p",null,[t("Code 1950, § 3-709.11; 1962, c. 126; 1966, c. 702, § 3.1-980; 2008, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860",target:"_blank",rel:"noreferrer"},"860"),t(".")],-1),c=[o,s,h];function d(l,f,g,w,m,p){return a(),r("div",null,c)}const y=i(n,[["render",d]]);export{u as __pageData,y as default};
