import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 3.2-6024 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6024.md","filePath":"vacode/3.2-6024.md"}'),n={name:"vacode/3.2-6024.md"},r=i('<h1 id="§-3-2-6024-definitions" tabindex="-1">§ 3.2-6024 Definitions. <a class="header-anchor" href="#§-3-2-6024-definitions" aria-label="Permalink to &quot;§ 3.2-6024 Definitions.&quot;">​</a></h1><p>As used in this article unless the context requires a different meaning:</p><p>&quot;Composting&quot; means the natural process in which beneficial microbes reduce dead poultry into a biologically safe by-product.</p><p>&quot;Dead poultry&quot; means poultry, exclusive of those intentionally slaughtered, that die as a result of disease, injury, or of natural causes, upon any premises in the Commonwealth.</p><p>&quot;Disposal&quot; means to put dead poultry into a landfill or the complete destruction of dead poultry in an incinerator or a disposal pit, or by rendering or composting.</p><p>&quot;Disposal pit&quot; means an opening dug in the ground to a minimum depth of six feet, containing a minimum capacity of 150 cubic feet, covered with a minimum of 12 inches of dirt, and provided with one or more openings for the introduction of poultry therein. Openings shall be of a minimum size of eight inches square and equipped with tight lids.</p><p>&quot;Incinerator&quot; means a device designed for treatment of waste by combustion.</p><p>&quot;Landfill&quot; means an area permitted by the Department of Environmental Quality allowing the disposal of dead poultry.</p><p>&quot;Person&quot; means any person who engages in the raising or keeping of poultry for profit in the Commonwealth.</p><p>&quot;Poultry&quot; means all chickens, ducks, turkeys, or other domestic fowls being raised or kept on any premises in the Commonwealth for profit.</p><p>&quot;Premises&quot; means the entire tract of land including the buildings thereon, owned, leased, or used by any person for the raising or keeping of poultry for profit.</p><p>&quot;Raising or keeping of poultry for profit&quot; means the raising or keeping of 500 or more poultry at one time for the purpose of sale of such poultry or the eggs produced therefrom.</p><p>&quot;Rendering&quot; means treating dead poultry according to the process described in 9 C.F.R. § 82.1.</p><p>1992, c. 101, § 3.1-742.1; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',14),a=[r];function s(p,u,d,l,c,f){return o(),t("div",null,a)}const g=e(n,[["render",s]]);export{h as __pageData,g as default};
