import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-6037 Grounds for denial, suspension, or revocation of license.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6037.md","filePath":"vacode/3.2-6037.md"}'),a={name:"vacode/3.2-6037.md"},n=t('<h1 id="§-3-2-6037-grounds-for-denial-suspension-or-revocation-of-license" tabindex="-1">§ 3.2-6037 Grounds for denial, suspension, or revocation of license. <a class="header-anchor" href="#§-3-2-6037-grounds-for-denial-suspension-or-revocation-of-license" aria-label="Permalink to &quot;§ 3.2-6037 Grounds for denial, suspension, or revocation of license.&quot;">​</a></h1><p>The Department may deny, suspend, or revoke a license to operate a shooting enclosure, if the:</p><ol><li><p>Applicant for a license does not own or lease the land that will be used for the shooting enclosure;</p></li><li><p>Applicant for a license does not meet local zoning and land-use requirements;</p></li><li><p>Operation of the shooting enclosure poses a threat to the health of humans, wildlife, or livestock;</p></li><li><p>Operation of the enclosure poses a threat of harm to: (i) wildlife species, whether native or naturalized; (ii) agricultural practices; or (iii) livestock;</p></li><li><p>Shooting enclosure is constructed or maintained in such a way that animals being held may escape; or</p></li><li><p>Applicant for a license or the licensee fails to meet any requirement of this article or regulations adopted pursuant thereto.</p></li></ol><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>, § 3.1-763.5:3; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),r=[n];function s(l,c,p,d,h,f){return o(),i("div",null,r)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
