import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-6039 Board to adopt regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6039.md","filePath":"vacode/3.2-6039.md"}'),i={name:"vacode/3.2-6039.md"},n=t('<h1 id="§-3-2-6039-board-to-adopt-regulations" tabindex="-1">§ 3.2-6039 Board to adopt regulations. <a class="header-anchor" href="#§-3-2-6039-board-to-adopt-regulations" aria-label="Permalink to &quot;§ 3.2-6039 Board to adopt regulations.&quot;">​</a></h1><p>A. The Board shall adopt regulations to carry out the provisions of this article including the requirements for licensing and operating shooting enclosures located within the Commonwealth. In addition, the Board may adopt regulations governing the veterinary care to be provided to animals held in shooting enclosures.</p><p>B. In adopting such regulations, the Board shall establish criteria for the following:</p><ol><li><p>Specific species of goats, sheep, and hogs that may be held;</p></li><li><p>Minimum contiguous acreage necessary;</p></li><li><p>Humane care and humane killing of animals being held;</p></li><li><p>Methods and procedures for disposal of animals;</p></li><li><p>Reporting the death of every animal being held in the shooting enclosure not killed by the clientele; and</p></li><li><p>Ensuring the reasonable utilization of all animals killed by the clientele.</p></li></ol><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0822" target="_blank" rel="noreferrer">822</a>, § 3.1-763.5:5; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),r=[n];function l(s,d,p,c,h,g){return a(),o("div",null,r)}const f=e(i,[["render",l]]);export{_ as __pageData,f as default};
