import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-6044 Civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6044.md","filePath":"vacode/3.2-6044.md"}'),o={name:"vacode/3.2-6044.md"},n=i('<h1 id="§-3-2-6044-civil-penalty" tabindex="-1">§ 3.2-6044 Civil penalty. <a class="header-anchor" href="#§-3-2-6044-civil-penalty" aria-label="Permalink to &quot;§ 3.2-6044 Civil penalty.&quot;">​</a></h1><p>Except as provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-6023/" target="_blank" rel="noreferrer">3.2-6023</a>, in lieu of any criminal penalty established pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-6043/" target="_blank" rel="noreferrer">3.2-6043</a>, the Board may assess a civil penalty in an amount not to exceed $1,000 per violation. In determining the amount of any civil penalty, the Board shall give due consideration to the (i) history of the person&#39;s previous violations, (ii) seriousness of the violation, and (iii) demonstrated good faith of the person charged in attempting to achieve compliance with this chapter after notification of the violation. Such civil penalties shall be deposited into the Livestock and Poultry Disease Fund established in § <a href="http://law.lis.virginia.gov/vacode/3.2-6045/" target="_blank" rel="noreferrer">3.2-6045</a>. The provisions of this section shall not apply to § <a href="http://law.lis.virginia.gov/vacode/3.2-6023/" target="_blank" rel="noreferrer">3.2-6023</a>.</p><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0563" target="_blank" rel="noreferrer">563</a>.</p>',3),r=[n];function l(s,c,h,p,d,v){return a(),t("div",null,r)}const g=e(o,[["render",l]]);export{f as __pageData,g as default};
