import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 3.2-6203 Liability of equine activity sponsors, equine professionals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6203.md","filePath":"vacode/3.2-6203.md"}'),o={name:"vacode/3.2-6203.md"},n=a('<h1 id="§-3-2-6203-liability-of-equine-activity-sponsors-equine-professionals" tabindex="-1">§ 3.2-6203 Liability of equine activity sponsors, equine professionals. <a class="header-anchor" href="#§-3-2-6203-liability-of-equine-activity-sponsors-equine-professionals" aria-label="Permalink to &quot;§ 3.2-6203 Liability of equine activity sponsors, equine professionals.&quot;">​</a></h1><p>No provision of this chapter shall prevent or limit the liability of an equine activity sponsor or equine professional or any other person who:</p><ol><li><p>Intentionally injures the participant;</p></li><li><p>Commits an act or omission that constitutes negligence for the safety of the participant and such act or omission caused the injury, unless such participant, parent or guardian has expressly assumed the risk causing the injury in accordance with subsection B of § <a href="http://law.lis.virginia.gov/vacode/3.2-6202/" target="_blank" rel="noreferrer">3.2-6202</a>; or</p></li><li><p>Knowingly provides faulty equipment or tack and such equipment or tack was faulty to the extent that it did cause the injury or death of the participant.</p></li></ol><p>1991, c. 358, § 3.1-796.133; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0876" target="_blank" rel="noreferrer">876</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),s=[n];function r(l,c,p,h,u,f){return i(),t("div",null,s)}const y=e(o,[["render",r]]);export{d as __pageData,y as default};
