import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse(`{"title":"§ 3.2-6502 State Veterinarian's power to inspect premises where animals are kept; investigations and search warrants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6502.md","filePath":"vacode/3.2-6502.md"}`),i={name:"vacode/3.2-6502.md"},n=t('<h1 id="§-3-2-6502-state-veterinarian-s-power-to-inspect-premises-where-animals-are-kept-investigations-and-search-warrants" tabindex="-1">§ 3.2-6502 State Veterinarian&#39;s power to inspect premises where animals are kept; investigations and search warrants. <a class="header-anchor" href="#§-3-2-6502-state-veterinarian-s-power-to-inspect-premises-where-animals-are-kept-investigations-and-search-warrants" aria-label="Permalink to &quot;§ 3.2-6502 State Veterinarian&#39;s power to inspect premises where animals are kept; investigations and search warrants.&quot;">​</a></h1><p>A. The State Veterinarian and each State Veterinarian&#39;s representative shall have the power to conduct inspections of public and private animal shelters, and inspect any business premises where animals are housed or kept, including any boarding establishment, kennel, pet shop, or the business premises of any dealer, exhibitor or groomer, at any reasonable time, for the purposes of determining if a violation of: (i) this chapter; (ii) any other state law governing the care, control or protection of animals; or (iii) any other state law governing property rights in animals has occurred.</p><p>B. Provisions for investigation of suspected violations of this chapter and other laws pertaining to animals are provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-6564/" target="_blank" rel="noreferrer">3.2-6564</a>. Provisions for obtaining a warrant and the power of search for violations of animal cruelty laws are provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-6568/" target="_blank" rel="noreferrer">3.2-6568</a>.</p><p>1993, c. 601, § 3.1-796.67:2; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',4),s=[n];function o(l,p,c,h,g,d){return a(),r("div",null,s)}const _=e(i,[["render",o]]);export{v as __pageData,_ as default};
