import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6503 Care of companion animals by owner; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6503.md","filePath":"vacode/3.2-6503.md"}'),n={name:"vacode/3.2-6503.md"},o=i('<h1 id="§-3-2-6503-care-of-companion-animals-by-owner-penalty" tabindex="-1">§ 3.2-6503 Care of companion animals by owner; penalty. <a class="header-anchor" href="#§-3-2-6503-care-of-companion-animals-by-owner-penalty" aria-label="Permalink to &quot;§ 3.2-6503 Care of companion animals by owner; penalty.&quot;">​</a></h1><p>A. Each owner shall provide for each of his companion animals:</p><ol><li><p>Adequate feed;</p></li><li><p>Adequate water;</p></li><li><p>Adequate shelter that is properly cleaned;</p></li><li><p>Adequate space in the primary enclosure for the particular type of animal depending upon its age, size, species, and weight;</p></li><li><p>Adequate exercise;</p></li><li><p>Adequate care, treatment, and transportation; and</p></li><li><p>Veterinary care when needed to prevent suffering or disease transmission.</p></li></ol><p>The provisions of this section shall also apply to every public or private animal shelter, or other releasing agency, and every foster care provider, dealer, pet shop, exhibitor, kennel, groomer, and boarding establishment. This section shall not require that animals used as food for other animals be euthanized.</p><p>B. Violation of this section is a Class 4 misdemeanor. A second or subsequent violation of subdivision A 1, 2, 3, or 7 is a Class 2 misdemeanor and a second or subsequent violation of subdivision A 4, 5, or 6 is a Class 3 misdemeanor.</p><p>1984, c. 492, § 29-213.38; 1987, c. 488, § 3.1-796.68; 1991, c. 348; 1993, c. 174; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0249" target="_blank" rel="noreferrer">249</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0787" target="_blank" rel="noreferrer">787</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0875" target="_blank" rel="noreferrer">875</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',6),t=[o];function l(s,p,c,d,f,h){return a(),r("div",null,t)}const _=e(n,[["render",l]]);export{m as __pageData,_ as default};
