import{_ as e,o as a,c as i,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 3.2-6507.2 Commercial dog breeding; requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6507.2.md","filePath":"vacode/3.2-6507.2.md"}'),n={name:"vacode/3.2-6507.2.md"},o=r('<h1 id="§-3-2-6507-2-commercial-dog-breeding-requirements" tabindex="-1">§ 3.2-6507.2 Commercial dog breeding; requirements. <a class="header-anchor" href="#§-3-2-6507-2-commercial-dog-breeding-requirements" aria-label="Permalink to &quot;§ 3.2-6507.2 Commercial dog breeding; requirements.&quot;">​</a></h1><p>Commercial dog breeders shall:</p><ol><li><p>Maintain no more than 50 dogs over the age of one year at any time for breeding purposes. However, a higher number of dogs may be allowed if approved by local ordinance after a public hearing. Any such ordinance may include additional requirements for commercial breeding operations;</p></li><li><p>Breed female dogs only: (i) after annual certification by a licensed veterinarian that the dog is in suitable health for breeding; (ii) after the dog has reached the age of 18 months; and (iii) if the dog has not yet reached the age of 8 years;</p></li><li><p>Dispose of dogs only by gift, sale, transfer, barter, or euthanasia by a licensed veterinarian;</p></li><li><p>Dispose of deceased dogs in accordance with § <a href="http://law.lis.virginia.gov/vacode/3.2-6554/" target="_blank" rel="noreferrer">3.2-6554</a>;</p></li><li><p>Dispose of dog waste in accordance with state and federal laws and regulations; and</p></li><li><p>Maintain accurate records for at least five years including:</p></li></ol><p>a. The date on which a dog enters the operation;</p><p>b. The person from whom the animal was purchased or obtained, including the address and phone number of such person;</p><p>c. A description of the animal, including the species, color, breed, sex, and approximate age and weight;</p><p>d. Any tattoo, microchip number, or other identification number carried by or appearing on the animal;</p><p>e. Each date that puppies were born to such animal and the number of puppies;</p><p>f. All medical care and vaccinations provided to the animal, including certifications required by a licensed veterinarian under this chapter; and</p><p>g. The disposition of each animal and the date.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>, § 3.1-796.77:2.</p>',11),t=[o];function d(s,c,l,p,h,m){return a(),i("div",null,t)}const u=e(n,[["render",d]]);export{f as __pageData,u as default};
