import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6511.1 Pet shops; procurement of dogs; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6511.1.md","filePath":"vacode/3.2-6511.1.md"}'),a={name:"vacode/3.2-6511.1.md"},i=t('<h1 id="§-3-2-6511-1-pet-shops-procurement-of-dogs-penalty" tabindex="-1">§ 3.2-6511.1 Pet shops; procurement of dogs; penalty. <a class="header-anchor" href="#§-3-2-6511-1-pet-shops-procurement-of-dogs-penalty" aria-label="Permalink to &quot;§ 3.2-6511.1 Pet shops; procurement of dogs; penalty.&quot;">​</a></h1><p>A. A pet shop shall sell or offer for adoption a dog procured only from a humane society; a private or public animal shelter as those terms are defined in § <a href="/vacode/3.2-6500/">3.2-6500</a>; or a person who has not received from the U.S. Department of Agriculture, pursuant to enforcement of the federal Animal Welfare Act (7 U.S.C. § 2131 et seq.) or regulations adopted thereunder, (i) a citation for a direct or critical violation or citations for three or more indirect or noncritical violations for at least two years prior to the procurement of the dog or (ii) two consecutive citations for no access to the facility prior to the procurement of the dog and who has not knowingly obtained the dog directly or indirectly from a person with such citations.</p><p>B. It is unlawful for any dealer or commercial dog breeder who is not licensed or exempted from licensure by the U.S. Department of Agriculture pursuant to the federal Animal Welfare Act (7 U.S.C. § 2131 et seq.) or regulations adopted thereunder to sell any dog to a pet shop.</p><p>C. A pet shop shall retain records verifying compliance with this section for a minimum of two years after the disposition of any dog.</p><p>D. No person shall serve as an owner, director, officer, manager, operator, member of staff, or animal caregiver of a pet shop if such person has been convicted of a violation of § <a href="/vacode/3.2-6570/">3.2-6570</a>.</p><p>E. Prior to selling or giving for adoption any dog, a pet shop shall obtain a signed statement from the purchaser or adopter specifying that such person has never been convicted of a violation of § <a href="/vacode/3.2-6570/">3.2-6570</a>.</p><p>F. Any person violating any provision of subsections A, B, C, or E of this section is guilty of a Class 1 misdemeanor for each dog sold or offered for sale. Any person violating any provision of subsection D of this section is guilty of a Class 1 misdemeanor.</p><p>2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>, § 3.1-796.71:1; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0679" target="_blank" rel="noreferrer">679</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0339" target="_blank" rel="noreferrer">339</a>.</p>',8),n=[i];function s(c,l,p,f,h,d){return o(),r("div",null,n)}const u=e(a,[["render",s]]);export{m as __pageData,u as default};
