import{_ as e,o as r,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6511.2 Dealers; importation and sale of dogs and cats; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6511.2.md","filePath":"vacode/3.2-6511.2.md"}'),t={name:"vacode/3.2-6511.2.md"},i=o('<h1 id="§-3-2-6511-2-dealers-importation-and-sale-of-dogs-and-cats-penalty" tabindex="-1">§ 3.2-6511.2 Dealers; importation and sale of dogs and cats; penalty. <a class="header-anchor" href="#§-3-2-6511-2-dealers-importation-and-sale-of-dogs-and-cats-penalty" aria-label="Permalink to &quot;§ 3.2-6511.2 Dealers; importation and sale of dogs and cats; penalty.&quot;">​</a></h1><p>A. No dealer, commercial dog breeder, or cat breeder shall import for sale, sell, or offer for sale, including sale for experimental purposes, any dog or cat bred by a person who has received from the U.S. Department of Agriculture, pursuant to enforcement of the federal Animal Welfare Act (7 U.S.C. § 2131 et seq.) or regulations adopted thereunder, (i) a citation for a direct or critical violation or citations for three or more indirect or noncritical violations for at least two years prior to the procurement of the dog or cat or (ii) two consecutive citations for no access to the facility prior to the procurement of the dog or cat.</p><p>B. No person shall serve as an owner, director, officer, manager, operator, member of staff, or animal caregiver for a dealer, commercial dog breeder, or cat breeder if such person has been convicted of a violation of § <a href="/vacode/3.2-6570/">3.2-6570</a>.</p><p>C. Any person violating any provision of this section is guilty of a Class 1 misdemeanor for each dog or cat imported, sold, or offered for sale.</p><p>D. As used in this section, &quot;dealer,&quot; &quot;commercial dog breeder,&quot; or &quot;cat breeder&quot; includes any person or entity that breeds dogs or cats regulated under federal law as research animals.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0569" target="_blank" rel="noreferrer">569</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?211+ful+CHAP0339" target="_blank" rel="noreferrer">339</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0094" target="_blank" rel="noreferrer">94</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0095" target="_blank" rel="noreferrer">95</a>.</p>',6),n=[i];function s(l,c,d,f,p,g){return r(),a("div",null,n)}const u=e(t,[["render",s]]);export{m as __pageData,u as default};
