import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-6513.2 Rental or lease of dog or cat prohibited; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6513.2.md","filePath":"vacode/3.2-6513.2.md"}'),i={name:"vacode/3.2-6513.2.md"},n=t('<h1 id="§-3-2-6513-2-rental-or-lease-of-dog-or-cat-prohibited-civil-penalty" tabindex="-1">§ 3.2-6513.2 Rental or lease of dog or cat prohibited; civil penalty. <a class="header-anchor" href="#§-3-2-6513-2-rental-or-lease-of-dog-or-cat-prohibited-civil-penalty" aria-label="Permalink to &quot;§ 3.2-6513.2 Rental or lease of dog or cat prohibited; civil penalty.&quot;">​</a></h1><p>A. As used in this section, &quot;covered person&quot; means any pet shop, commercial dog breeder, pet dealer, firm, or other pet selling business.</p><p>B. The rental or leasing of a dog or cat to a Virginia consumer, including by a purported sale of the animal in such a manner as to vest less than full equity in the consumer at the time of the purported sale, is prohibited.</p><p>C. No covered person shall offer in Virginia an agreement for the transfer or sale of a dog or cat to the consumer in which the animal is subject to repossession in any manner upon default of the agreement by the consumer.</p><p>D. No financial institution, as defined in § <a href="/vacode/6.2-100/">6.2-100</a>, shall offer in Virginia a loan or financing agreement for the rental, lease, or sale of a dog or cat where the animal is subject to repossession upon default under the terms of the financing agreement.</p><p>E. Any violation of this section shall also constitute a prohibited practice under § <a href="/vacode/59.1-200/">59.1-200</a> and shall be subject to the enforcement provisions of the Virginia Consumer Protection Act (§ <a href="/vacode/59.1-196/">59.1-196</a> et seq.). In addition, any covered person that violates any provision of this section may have its business license, retail license, or local pet shop permit issued pursuant to § <a href="/vacode/3.2-6537/">3.2-6537</a> suspended or revoked after a hearing by the issuing authority. The court may also suspend or revoke the retail license of any business found to be in violation of this section.</p><p>F. The provisions of this section shall not apply to the temporary rental or lease of any of the following animals, so long as the animal is used in accordance with applicable federal, state, and local animal protection laws:</p><ol><li><p>A purebred dog that is rented for the express purpose of breeding pursuant to a written lease that sets out a specific time period, contains a firm end date, and is recorded with a national purebred dog registry;</p></li><li><p>A dog or cat that is used in spectator events, shows, exhibitions, motion pictures, or other entertainment, including animal exhibitions, racing events, field trials, polo matches, rodeo events, or any audiovisual media; or</p></li><li><p>A service dog as defined in § <a href="/vacode/51.5-40.1/">51.5-40.1</a>, guide or leader dog as defined in § <a href="/vacode/3.2-6588/">3.2-6588</a>, security dog, police or law-enforcement dog, military working dog, or certified facility dog as defined in § <a href="/vacode/18.2-67.9:1/">18.2-67.9:1</a>.</p></li></ol><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0630" target="_blank" rel="noreferrer">630</a>.</p>',9),r=[n];function s(l,d,c,p,h,f){return o(),a("div",null,r)}const m=e(i,[["render",s]]);export{g as __pageData,m as default};
