import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6515 Written notice of consumer remedies required to be supplied by pet dealers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6515.md","filePath":"vacode/3.2-6515.md"}'),i={name:"vacode/3.2-6515.md"},o=r('<h1 id="§-3-2-6515-written-notice-of-consumer-remedies-required-to-be-supplied-by-pet-dealers" tabindex="-1">§ 3.2-6515 Written notice of consumer remedies required to be supplied by pet dealers. <a class="header-anchor" href="#§-3-2-6515-written-notice-of-consumer-remedies-required-to-be-supplied-by-pet-dealers" aria-label="Permalink to &quot;§ 3.2-6515 Written notice of consumer remedies required to be supplied by pet dealers.&quot;">​</a></h1><p>A. A pet dealer shall give the notice hereinafter set forth in writing to a consumer prior to the delivery of a dog or cat. Such notice shall be embodied in a written contract, the pet dealer&#39;s animal history certificate, or a separate document and shall state in ten-point boldface type the following:</p><p>&quot;NOTICE</p><p>The sale of dogs and cats is subject to the provisions of the Virginia Consumer Protection Act (§ <a href="/vacode/59.1-196/">59.1-196</a> et seq.). In the event that a licensed veterinarian certifies your animal to be unfit for purchase within 10 days following receipt of your animal, or within 14 days following receipt if the animal is infected with parvovirus, you may choose: (i) to return your animal, or in the case of an animal that has died, the veterinary certification, and receive a refund of the purchase price including sales tax; or (ii) to return the animal and receive an exchange animal of your choice of equivalent value. In the case of an animal purchased from a pet shop or a USDA licensed dealer, you also may choose to retain the animal and receive reimbursement of the cost of veterinary certification and veterinary fees in an amount up to the purchase price of the animal.</p><p>In order to exercise these rights you must present a written veterinary certification that the animal is unfit to the pet dealer within three business days after receiving such certification.</p><p>If the pet dealer has promised to register your animal or to provide the papers necessary therefor and fails to do so within 120 days following the date of contract, you are entitled to return the animal and receive a refund of the purchase price or to retain the animal and receive a refund of an amount not to exceed 50 percent of the purchase price.&quot;</p><p>B. Any violation of this section shall also constitute a prohibited practice under § <a href="/vacode/59.1-200/">59.1-200</a> and shall be subject to the enforcement provisions of the Virginia Consumer Protection Act (§ <a href="/vacode/59.1-196/">59.1-196</a> et seq.).</p><p>1984, c. 492, § 29-213.51; 1987, c. 488, § 3.1-796.81; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0448" target="_blank" rel="noreferrer">448</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>.</p>',8),n=[o];function s(c,l,d,h,p,f){return t(),a("div",null,n)}const v=e(i,[["render",s]]);export{m as __pageData,v as default};
