import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-6530 When license tax payable.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6530.md","filePath":"vacode/3.2-6530.md"}'),n={name:"vacode/3.2-6530.md"},i=t('<h1 id="§-3-2-6530-when-license-tax-payable" tabindex="-1">§ 3.2-6530 When license tax payable. <a class="header-anchor" href="#§-3-2-6530-when-license-tax-payable" aria-label="Permalink to &quot;§ 3.2-6530 When license tax payable.&quot;">​</a></h1><p>A. The license tax as prescribed in § <a href="http://law.lis.virginia.gov/vacode/3.2-6528/" target="_blank" rel="noreferrer">3.2-6528</a> is due not later than 30 days after a dog or cat has reached the age of four months, or not later than 30 days after an owner acquires a dog or cat four months of age or older, and each year thereafter.</p><p>Licensing periods for individual dogs and cats may be equal to and may run concurrently with the rabies vaccination effective period.</p><p>B. The governing body of a county or city may by ordinance provide for a lifetime dog or cat license. Such a license shall be valid only as long as the animal&#39;s owner resides in the issuing locality and the animal&#39;s rabies vaccination is kept current.</p><p>C. Any kennel license tax prescribed pursuant to § <a href="http://law.lis.virginia.gov/vacode/3.2-6528/" target="_blank" rel="noreferrer">3.2-6528</a> shall be due on January 1 and not later than January 31 of each year.</p><p>1984, cc. 248, 492, § 29-213.58; 1986, c. 169; 1987, c. 488, § 3.1-796.88; 1990, c. 365; 1993, c. 817; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0836" target="_blank" rel="noreferrer">836</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0567" target="_blank" rel="noreferrer">567</a>.</p>',6),o=[i];function l(c,s,h,d,p,f){return a(),r("div",null,o)}const u=e(n,[["render",l]]);export{_ as __pageData,u as default};
