import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-6534 Disposition of funds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6534.md","filePath":"vacode/3.2-6534.md"}'),t={name:"vacode/3.2-6534.md"},i=n('<h1 id="§-3-2-6534-disposition-of-funds" tabindex="-1">§ 3.2-6534 Disposition of funds. <a class="header-anchor" href="#§-3-2-6534-disposition-of-funds" aria-label="Permalink to &quot;§ 3.2-6534 Disposition of funds.&quot;">​</a></h1><p>Unless otherwise provided by ordinance of the local governing body, the treasurer of each locality shall keep all moneys collected by him for dog and cat license taxes in a separate account from all other funds collected by him. The locality shall use the funds for the following purposes:</p><ol><li><p>The salary and expenses of the animal control officer and necessary staff;</p></li><li><p>The care and maintenance of a public animal shelter;</p></li><li><p>The maintenance of a rabies control program;</p></li><li><p>Payments as a bounty to any person neutering or spaying a dog up to the amount of one year of the license tax as provided by ordinance;</p></li><li><p>Payments for compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/3.2-6553/" target="_blank" rel="noreferrer">3.2-6553</a>; and</p></li><li><p>Efforts to promote sterilization of dogs and cats.</p></li></ol><p>Any part or all of any surplus remaining in such account on December 31 of any year may be transferred by the governing body of such locality into the general fund of such locality.</p><p>1984, c. 492, § 29-213.70; 1987, c. 488, § 3.1-796.101; 1993, c. 959; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',5),r=[i];function l(s,c,p,f,d,h){return a(),o("div",null,r)}const g=e(t,[["render",l]]);export{_ as __pageData,g as default};
