import{_ as e,o as a,c as n,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 3.2-6540.02 Notice of dangerous dog finding; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6540.02.md","filePath":"vacode/3.2-6540.02.md"}'),t={name:"vacode/3.2-6540.02.md"},i=o('<h1 id="§-3-2-6540-02-notice-of-dangerous-dog-finding-penalty" tabindex="-1">§ 3.2-6540.02 Notice of dangerous dog finding; penalty. <a class="header-anchor" href="#§-3-2-6540-02-notice-of-dangerous-dog-finding-penalty" aria-label="Permalink to &quot;§ 3.2-6540.02 Notice of dangerous dog finding; penalty.&quot;">​</a></h1><p>A. Any releasing agency transferring or releasing for adoption within the Commonwealth an animal found to be a dangerous dog pursuant to § <a href="/vacode/3.2-6540/">3.2-6540</a> shall notify in writing the receiving party of the requirements of this section and §§ <a href="/vacode/3.2-6540/">3.2-6540</a>, <a href="/vacode/3.2-6540.01/">3.2-6540.01</a>, <a href="/vacode/3.2-6540.03/">3.2-6540.03</a>, and <a href="/vacode/3.2-6540.04/">3.2-6540.04</a>.</p><p>B. Any releasing agency transferring or releasing for adoption outside the Commonwealth an animal found to be a dangerous dog pursuant to § <a href="/vacode/3.2-6540/">3.2-6540</a> shall notify the appropriate animal control officer in the receiving jurisdiction that the animal has been so adjudicated.</p><p>C. Any owner of an animal found to be a dangerous dog in another state shall, upon bringing such animal to reside within the Commonwealth, notify the animal control officer of the jurisdiction in which the owner resides that the animal has been so adjudicated.</p><p>D. Any owner who disposes by surrender to a releasing agency, gift, sale, transfer, or trade of an animal found to be a dangerous dog pursuant to § <a href="/vacode/3.2-6540/">3.2-6540</a> shall notify the receiver in writing that the animal has been so adjudicated. A violation of this subsection is a Class 3 misdemeanor.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0464" target="_blank" rel="noreferrer">464</a>.</p>',6),r=[i];function s(d,c,h,l,f,g){return a(),n("div",null,r)}const _=e(t,[["render",s]]);export{u as __pageData,_ as default};
