import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 3.2-6540.04 Subsequent attack or bite by dangerous dog; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6540.04.md","filePath":"vacode/3.2-6540.04.md"}'),n={name:"vacode/3.2-6540.04.md"},s=o('<h1 id="§-3-2-6540-04-subsequent-attack-or-bite-by-dangerous-dog-penalty" tabindex="-1">§ 3.2-6540.04 Subsequent attack or bite by dangerous dog; penalty. <a class="header-anchor" href="#§-3-2-6540-04-subsequent-attack-or-bite-by-dangerous-dog-penalty" aria-label="Permalink to &quot;§ 3.2-6540.04 Subsequent attack or bite by dangerous dog; penalty.&quot;">​</a></h1><p>A. Any owner of an animal found to be a dangerous dog pursuant to § <a href="/vacode/3.2-6540/">3.2-6540</a>, when such finding arose out of a separate and distinct incident, is guilty of a:</p><ol><li><p>Class 2 misdemeanor if such dog attacks and injures or kills a cat or dog that is a companion animal belonging to another person; or</p></li><li><p>Class 1 misdemeanor if such dog bites a human being or attacks a human being causing bodily injury.</p></li></ol><p>B. The provisions of subsection A shall not apply to any animal that at the time of the act complained of was responding to pain or injury, was protecting itself, its kennel, its offspring, a person, or its owner&#39;s property, or was a police dog engaged in the performance of its duties at the time of the attack.</p><p>C. The court may determine that a person convicted under this section shall be prohibited from owning, possessing, or residing on the same property with a dog.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0464" target="_blank" rel="noreferrer">464</a>.</p>',6),i=[s];function r(d,c,p,l,u,g){return a(),t("div",null,i)}const f=e(n,[["render",r]]);export{_ as __pageData,f as default};
