import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6550 Requirements for foster homes; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6550.md","filePath":"vacode/3.2-6550.md"}'),r={name:"vacode/3.2-6550.md"},n=a('<h1 id="§-3-2-6550-requirements-for-foster-homes-penalty" tabindex="-1">§ 3.2-6550 Requirements for foster homes; penalty. <a class="header-anchor" href="#§-3-2-6550-requirements-for-foster-homes-penalty" aria-label="Permalink to &quot;§ 3.2-6550 Requirements for foster homes; penalty.&quot;">​</a></h1><p>In addition to any other requirements of this chapter, foster homes shall be subject to the following:</p><ol><li><p>No foster home shall be operated in violation of any local zoning ordinance; and</p></li><li><p>No private residential dwelling and its surrounding grounds that serves as a foster home shall keep more than 50 companion animals on site at one time.</p></li></ol><p>Any foster home found in violation of this section may be subject to a civil penalty not to exceed $250.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1007" target="_blank" rel="noreferrer">1007</a>, § 3.1-796.96:6; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0148" target="_blank" rel="noreferrer">148</a>.</p>',5),i=[n];function s(l,c,p,f,h,_){return t(),o("div",null,i)}const u=e(r,[["render",s]]);export{m as __pageData,u as default};
