import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-6564 Complaint of suspected violation; investigation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6564.md","filePath":"vacode/3.2-6564.md"}'),i={name:"vacode/3.2-6564.md"},n=t('<h1 id="§-3-2-6564-complaint-of-suspected-violation-investigation" tabindex="-1">§ 3.2-6564 Complaint of suspected violation; investigation. <a class="header-anchor" href="#§-3-2-6564-complaint-of-suspected-violation-investigation" aria-label="Permalink to &quot;§ 3.2-6564 Complaint of suspected violation; investigation.&quot;">​</a></h1><p>A. Upon receiving a complaint of a suspected violation of this chapter, any ordinance enacted pursuant to this chapter or any law for the protection of domestic animals, any animal control officer, law-enforcement officer, or State Veterinarian&#39;s representative may, for the purpose of investigating the allegations of the complaint, enter upon, during business hours, any business premises, including any place where animals or animal records are housed or kept, of any dealer, pet shop, groomer, or boarding establishment. Upon receiving a complaint of a suspected violation of any law or ordinance regarding care or treatment of animals or disposal of dead animals, any humane investigator may, for the purpose of investigating the allegations of the complaint, enter upon, during business hours, any business premises, including any place where animals or animal records are housed or kept, of any dealer, pet shop, groomer, or boarding establishment.</p><p>Upon obtaining a warrant as provided for in § <a href="http://law.lis.virginia.gov/vacode/3.2-6568/" target="_blank" rel="noreferrer">3.2-6568</a>, the law-enforcement officer, animal control officer, State Veterinarian&#39;s representative, or humane investigator may enter upon any other premises where the animal or animals described in the complaint are housed or kept. Attorneys for the Commonwealth and law-enforcement officials shall provide such assistance as may be required in the conduct of such investigations.</p><p>B. If the investigation discloses that a violation of § <a href="http://law.lis.virginia.gov/vacode/3.2-6503/" target="_blank" rel="noreferrer">3.2-6503</a> has occurred, the investigating official shall notify the owner or custodian of the complaint and of what action is necessary to comply with this chapter.</p><p>1984, c. 492, § 29-213.76; 1987, c. 488, § 3.1-796.107; 1991, c. 451; 1993, c. 174; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',5),r=[n];function s(c,l,p,h,f,d){return a(),o("div",null,r)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
