import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 3.2-6575 Sterilization agreement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6575.md","filePath":"vacode/3.2-6575.md"}'),r={name:"vacode/3.2-6575.md"},o=i('<h1 id="§-3-2-6575-sterilization-agreement" tabindex="-1">§ 3.2-6575 Sterilization agreement. <a class="header-anchor" href="#§-3-2-6575-sterilization-agreement" aria-label="Permalink to &quot;§ 3.2-6575 Sterilization agreement.&quot;">​</a></h1><p>Any agreement used by a releasing agency pursuant to subsection B of § <a href="http://law.lis.virginia.gov/vacode/3.2-6574/" target="_blank" rel="noreferrer">3.2-6574</a> shall contain:</p><ol><li><p>The date of the agreement;</p></li><li><p>The names, addresses, and signatures of the releasing agency and the new owner;</p></li><li><p>A description of the dog or cat to be adopted;</p></li><li><p>The date by which the dog or cat is required to be sterilized; and</p></li><li><p>A statement printed in conspicuous, bold print, that sterilization of the dog or cat is required under this article; that a person who violates this article is subject to a civil penalty; and that the new owner may be compelled to comply with the provisions of this article.</p></li></ol><p>1993, c. 959, § 3.1-796.126:2; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',4),n=[o];function s(l,c,d,p,h,_){return t(),a("div",null,n)}const f=e(r,[["render",s]]);export{m as __pageData,f as default};
