import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 3.2-6576 Sterilization confirmation; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/3.2-6576.md","filePath":"vacode/3.2-6576.md"}'),r={name:"vacode/3.2-6576.md"},n=a('<h1 id="§-3-2-6576-sterilization-confirmation-civil-penalty" tabindex="-1">§ 3.2-6576 Sterilization confirmation; civil penalty. <a class="header-anchor" href="#§-3-2-6576-sterilization-confirmation-civil-penalty" aria-label="Permalink to &quot;§ 3.2-6576 Sterilization confirmation; civil penalty.&quot;">​</a></h1><p>Each new owner who signs a sterilization agreement shall, within seven days of the sterilization, cause to be delivered or mailed to the releasing agency written confirmation signed by the veterinarian who performed the sterilization. The confirmation shall briefly describe the dog or cat; include the new owner&#39;s name and address; certify that the sterilization was performed; and specify the date of the procedure. Any person who violates this section shall be subject to a civil penalty not to exceed $150.</p><p>1993, c. 959, § 3.1-796.126:3; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0627" target="_blank" rel="noreferrer">627</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0672" target="_blank" rel="noreferrer">672</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0860" target="_blank" rel="noreferrer">860</a>.</p>',3),o=[n];function l(c,s,h,d,f,p){return t(),i("div",null,o)}const m=e(r,[["render",l]]);export{g as __pageData,m as default};
