import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-105 Prohibited contracts by legislators.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-105.md","filePath":"vacode/30-105.md"}'),i={name:"vacode/30-105.md"},o=r('<h1 id="§-30-105-prohibited-contracts-by-legislators" tabindex="-1">§ 30-105 Prohibited contracts by legislators. <a class="header-anchor" href="#§-30-105-prohibited-contracts-by-legislators" aria-label="Permalink to &quot;§ 30-105 Prohibited contracts by legislators.&quot;">​</a></h1><p>A. No legislator shall have a personal interest in a contract with the legislative branch of state government.</p><p>B. No legislator shall have a personal interest in a contract with any governmental agency of the executive or judicial branches of state government, other than in a contract of regular employment, unless such contract is awarded as a result of competitive sealed bidding or competitive negotiation as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-4302.1/" target="_blank" rel="noreferrer">2.2-4302.1</a> or <a href="http://law.lis.virginia.gov/vacode/2.2-4302.2/" target="_blank" rel="noreferrer">2.2-4302.2</a> or is exempted from competitive sealed bidding or competitive negotiation pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4344/" target="_blank" rel="noreferrer">2.2-4344</a>.</p><p>C. No legislator shall have a personal interest in a contract with any governmental agency of local government, other than in a contract of regular employment, unless such contract is (i) awarded as a result of competitive sealed bidding or competitive negotiation as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-4302.1/" target="_blank" rel="noreferrer">2.2-4302.1</a> or <a href="http://law.lis.virginia.gov/vacode/2.2-4302.2/" target="_blank" rel="noreferrer">2.2-4302.2</a> or is awarded as a result of a procedure embodying competitive principles as authorized by subdivision A 10 or A 11 of § <a href="http://law.lis.virginia.gov/vacode/2.2-4343/" target="_blank" rel="noreferrer">2.2-4343</a>; (ii) exempted from competitive sealed bidding, competitive negotiation, or a procedure embodying competitive principles pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-4344/" target="_blank" rel="noreferrer">2.2-4344</a>; or (iii) awarded after a finding, in writing, by the administrative head of the local governmental agency that competitive bidding or negotiation is contrary to the best interest of the public.</p><p>D. The provisions of this section shall not apply to contracts for the sale by a governmental agency of services or goods at uniform prices available to the general public.</p><p>E. The provisions of this section shall not apply to a legislator&#39;s personal interest in a contract between a public institution of higher education in the Commonwealth and a publisher or wholesaler of textbooks or other educational materials for students, which accrues to him solely because he has authored or otherwise created such textbooks or materials.</p><p>1987, Sp. Sess., c. 1, § 2.1-639.35; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0583" target="_blank" rel="noreferrer">583</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>.</p>',7),n=[o];function l(s,c,h,g,p,d){return t(),a("div",null,n)}const b=e(i,[["render",l]]);export{f as __pageData,b as default};
