import{_ as e,o as r,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-110 Disclosure.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-110.md","filePath":"vacode/30-110.md"}'),t={name:"vacode/30-110.md"},l=i('<h1 id="§-30-110-disclosure" tabindex="-1">§ 30-110 Disclosure. <a class="header-anchor" href="#§-30-110-disclosure" aria-label="Permalink to &quot;§ 30-110 Disclosure.&quot;">​</a></h1><p>A. In accordance with the requirements set forth in § <a href="http://law.lis.virginia.gov/vacode/30-111.1/" target="_blank" rel="noreferrer">30-111.1</a>, every legislator and legislator-elect shall file, as a condition to assuming office, a disclosure statement of his personal interests and such other information as is required on the form prescribed by the Council pursuant to § <a href="http://law.lis.virginia.gov/vacode/30-111/" target="_blank" rel="noreferrer">30-111</a> and thereafter shall file such a statement annually on or before February 1. Disclosure forms shall be made available by the Virginia Conflict of Interest and Ethics Advisory Council at least 30 days prior to the filing deadline. Disclosure forms shall be filed electronically with the Virginia Conflict of Interest and Ethics Advisory Council in accordance with the standards approved by it pursuant to § <a href="http://law.lis.virginia.gov/vacode/30-356/" target="_blank" rel="noreferrer">30-356</a>. The disclosure forms of the members of the General Assembly shall be maintained as public records for five years in the office of the Virginia Conflict of Interest and Ethics Advisory Council. Such forms shall be made public no later than six weeks after the filing deadline.</p><p>B. Candidates for the General Assembly shall file a disclosure statement of their personal interests as required by §§ <a href="http://law.lis.virginia.gov/vacode/24.2-500/" target="_blank" rel="noreferrer">24.2-500</a> through <a href="http://law.lis.virginia.gov/vacode/24.2-503/" target="_blank" rel="noreferrer">24.2-503</a>.</p><p>C. Any legislator who has a personal interest in any transaction pending before the General Assembly and who is disqualified from participating in that transaction pursuant to § <a href="http://law.lis.virginia.gov/vacode/30-108/" target="_blank" rel="noreferrer">30-108</a> and the rules of his house shall disclose his interest in accordance with the applicable rule of his house.</p><p>1987, Sp. Sess., c. 1, § 2.1-639.40; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0779" target="_blank" rel="noreferrer">779</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0832" target="_blank" rel="noreferrer">832</a>.</p>',5),n=[l];function o(s,c,h,f,g,d){return r(),a("div",null,n)}const v=e(t,[["render",o]]);export{u as __pageData,v as default};
