import{_ as e,o as a,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 30-123 Knowing violation of chapter a misdemeanor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-123.md","filePath":"vacode/30-123.md"}'),t={name:"vacode/30-123.md"},i=r('<h1 id="§-30-123-knowing-violation-of-chapter-a-misdemeanor" tabindex="-1">§ 30-123 Knowing violation of chapter a misdemeanor. <a class="header-anchor" href="#§-30-123-knowing-violation-of-chapter-a-misdemeanor" aria-label="Permalink to &quot;§ 30-123 Knowing violation of chapter a misdemeanor.&quot;">​</a></h1><p>Any legislator who knowingly violates any of the provisions of Articles 2 through 5 (§§ <a href="http://law.lis.virginia.gov/vacode/30-102/" target="_blank" rel="noreferrer">30-102</a> through <a href="http://law.lis.virginia.gov/vacode/30-111/" target="_blank" rel="noreferrer">30-111</a>) of this chapter shall be guilty of a Class 1 misdemeanor. A knowing violation under this section is one in which the person engages in conduct, performs an act or refuses to perform an act when he knows that the conduct is prohibited or required by this chapter. There shall be no prosecution for a violation of § <a href="http://law.lis.virginia.gov/vacode/30-108/" target="_blank" rel="noreferrer">30-108</a> or subsection C of § <a href="http://law.lis.virginia.gov/vacode/30-110/" target="_blank" rel="noreferrer">30-110</a> unless the house in which the member sits has referred the matter to the Attorney General as provided in subdivision 4 of § <a href="http://law.lis.virginia.gov/vacode/30-116/" target="_blank" rel="noreferrer">30-116</a>.</p><p>1987, Sp. Sess., c. 1, § 2.1-639.54; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),n=[i];function s(h,l,c,d,f,p){return a(),o("div",null,n)}const v=e(t,[["render",s]]);export{_ as __pageData,v as default};
