import{_ as e,o,c as i,O as a}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 30-171 Virginia Commission on Intergovernmental Cooperation; membership; staff; compensation and expenses; quorum.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-171.md","filePath":"vacode/30-171.md"}'),n={name:"vacode/30-171.md"},r=a('<h1 id="§-30-171-virginia-commission-on-intergovernmental-cooperation-membership-staff-compensation-and-expenses-quorum" tabindex="-1">§ 30-171 Virginia Commission on Intergovernmental Cooperation; membership; staff; compensation and expenses; quorum. <a class="header-anchor" href="#§-30-171-virginia-commission-on-intergovernmental-cooperation-membership-staff-compensation-and-expenses-quorum" aria-label="Permalink to &quot;§ 30-171 Virginia Commission on Intergovernmental Cooperation; membership; staff; compensation and expenses; quorum.&quot;">​</a></h1><p>A. The Virginia Commission on Intergovernmental Cooperation (the Commission) is established in the legislative branch of state government. The Commission shall consist of the following 14 members: (i) the six members of the Commission on Interstate Cooperation of the Senate, (ii) the six members of the Commission on Interstate Cooperation of the House of Delegates, and (iii) the Clerk of the Senate and the Clerk of the House of Delegates who shall serve as ex officio, nonvoting members of the Commission.</p><p>B. The chairman and vice-chairman of the Commission shall serve for a period of two years, and the chairmanship and vice-chairmanship shall alternate between the chairman of the Senate Commission on Interstate Cooperation and the chairman of the House Commission on Interstate Cooperation.</p><p>C. The Commission on Intergovernmental Cooperation shall select such officials of state government as it deems proper to serve as ex officio, nonvoting members of the Commission for terms of four years each.</p><p>D. The Division of Legislative Services shall furnish upon request such staff assistance and services to the Commission and its committees as may be required to carry forth the charge of the Commission.</p><p>E. Members of the Commission shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a> and shall be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. Funding for the costs of compensation and expenses of the members shall be provided from existing appropriations to the Commission.</p><p>F. A majority of the members of the Commission shall constitute a quorum. The Commission shall elect a chairman and vice-chairman from among its membership. Meetings of the Commission shall be held at the call of the chairman or whenever the majority of the members so request.</p><p>1936, pp. 369, 370; Michie Code 1942, § 311a; 1973, c. 538, §§ 9-55, 9-55.1; 1976, c. 61, § 9-53; 1978, c. 198; 1981, cc. 308, 315; 1983, c. 84; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0850" target="_blank" rel="noreferrer">850</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0879" target="_blank" rel="noreferrer">879</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0365" target="_blank" rel="noreferrer">365</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>.</p>',8),t=[r];function s(m,h,l,c,f,p){return o(),i("div",null,t)}const v=e(n,[["render",s]]);export{d as __pageData,v as default};
