import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-179 (Effective September 1, 2022) Powers and duties of the Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-179.md","filePath":"vacode/30-179.md"}'),t={name:"vacode/30-179.md"},n=a('<h1 id="§-30-179-effective-september-1-2022-powers-and-duties-of-the-council" tabindex="-1">§ 30-179 (Effective September 1, 2022) Powers and duties of the Council. <a class="header-anchor" href="#§-30-179-effective-september-1-2022-powers-and-duties-of-the-council" aria-label="Permalink to &quot;§ 30-179 (Effective September 1, 2022) Powers and duties of the Council.&quot;">​</a></h1><p>The Council shall:</p><ol><li><p>Furnish, upon request, advisory opinions or guidelines, and other appropriate information regarding the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.) to any person or public body, in an expeditious manner;</p></li><li><p>Conduct training seminars and educational programs for the members and staff of public bodies and other interested persons on the requirements of the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.);</p></li><li><p>Publish such educational materials as it deems appropriate on the provisions of the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.);</p></li><li><p>Request from any public body such assistance, services, and information as will enable the Council to effectively carry out its responsibilities. Information provided to the Council by a public body shall not be released to any other party unless authorized by such public body;</p></li><li><p>Assist in the development and implementation of the provisions of § <a href="/vacode/2.2-3704.1/">2.2-3704.1</a>;</p></li><li><p>Develop an online public comment form to be posted on the Council&#39;s official public government website, as defined in § <a href="/vacode/2.2-3701/">2.2-3701</a>, to enable any requester to comment on the quality of assistance provided to the requester by a public body; and</p></li><li><p>Report annually on or before December 1 of each year on its activities and findings regarding the Virginia Freedom of Information Act (§ <a href="/vacode/2.2-3700/">2.2-3700</a> et seq.), including recommendations for changes in the law, to the General Assembly and the Governor. The annual report shall be published as a state document.</p></li></ol><p>2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0917" target="_blank" rel="noreferrer">917</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0987" target="_blank" rel="noreferrer">987</a>, § <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>.1-346.3; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0730" target="_blank" rel="noreferrer">730</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0694" target="_blank" rel="noreferrer">694</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0645" target="_blank" rel="noreferrer">645</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0056" target="_blank" rel="noreferrer">56</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0354" target="_blank" rel="noreferrer">354</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0325" target="_blank" rel="noreferrer">325</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>.</p>',4),o=[n];function l(s,c,f,p,h,g){return i(),r("div",null,o)}const b=e(t,[["render",l]]);export{u as __pageData,b as default};
