import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 30-19.03:1.3 Evaluations to be prepared for legislation increasing or beginning regulation of an occupation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-19.03:1.3.md","filePath":"vacode/30-19.03:1.3.md"}'),n={name:"vacode/30-19.03:1.3.md"},i=t('<h1 id="§-30-19-03-1-3-evaluations-to-be-prepared-for-legislation-increasing-or-beginning-regulation-of-an-occupation" tabindex="-1">§ 30-19.03:1.3 Evaluations to be prepared for legislation increasing or beginning regulation of an occupation. <a class="header-anchor" href="#§-30-19-03-1-3-evaluations-to-be-prepared-for-legislation-increasing-or-beginning-regulation-of-an-occupation" aria-label="Permalink to &quot;§ 30-19.03:1.3 Evaluations to be prepared for legislation increasing or beginning regulation of an occupation.&quot;">​</a></h1><p>A. For the purposes of this section, &quot;regulation&quot; means any statement of general application, having the force of law and affecting the rights or conduct of any person, adopted by an agency in accordance with the authority conferred on it by the Constitution and applicable statutes of the Commonwealth.</p><p>B. When any legislative bill requiring the Department of Professional and Occupational Regulation to increase or begin regulation of an occupation is filed during any session of the General Assembly, the chairman of the committee having jurisdiction over the proposal shall request that the Board for Professional and Occupational Regulation (the Board) prepare an evaluation of the legislation using the criteria outlined in § <a href="/vacode/54.1-311/">54.1-311</a>.</p><p>Upon receipt of such a request, the Board shall prepare the evaluation and shall forward copies of such evaluation to the Clerk of the House of Delegates for House bills and to the Clerk of the Senate for Senate bills no later than November 1 of the same year for requests received during a regular session of the General Assembly or as soon as practicable for requests received during a special session of the General Assembly for transmittal to each patron of the legislation and to the chairman of each committee of the General Assembly to consider the same.</p><p>All departments, agencies of government, and the Division of Legislative Services are directed to make available such information and assistance as the Board may request in preparing the evaluations required by this section.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0812" target="_blank" rel="noreferrer">812</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0647" target="_blank" rel="noreferrer">647</a>.</p>',6),r=[i];function s(l,c,h,p,f,g){return a(),o("div",null,r)}const m=e(n,[["render",s]]);export{d as __pageData,m as default};
