import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 30-19.18 How distance ascertained from place other than courthouse.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-19.18.md","filePath":"vacode/30-19.18.md"}'),o={name:"vacode/30-19.18.md"},n=r('<h1 id="§-30-19-18-how-distance-ascertained-from-place-other-than-courthouse" tabindex="-1">§ 30-19.18 How distance ascertained from place other than courthouse. <a class="header-anchor" href="#§-30-19-18-how-distance-ascertained-from-place-other-than-courthouse" aria-label="Permalink to &quot;§ 30-19.18 How distance ascertained from place other than courthouse.&quot;">​</a></h1><p>For the purpose of this chapter and Chapter 1 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-100/" target="_blank" rel="noreferrer">2.2-100</a> et seq.) and §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2814/" target="_blank" rel="noreferrer">2.2-2814</a> of Title 2.2 only, the distance of the City of Richmond from any place in any county or city other than the courthouse thereof, shall be ascertained by adding to or deducting from the number of miles between the City of Richmond and such courthouse, as declared by law, so many miles as such place may be farther from or nearer to the City of Richmond than such courthouse may be.</p><p>Code 1950, § 14-7; 1964, c. 386, § 14.1-21; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>.</p>',3),c=[n];function h(i,s,d,l,f,_){return a(),t("div",null,c)}const m=e(o,[["render",h]]);export{u as __pageData,m as default};
