import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 30-19.1:7 Bills related to the Virginia Retirement System; impact statements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-19.1:7.md","filePath":"vacode/30-19.1:7.md"}'),n={name:"vacode/30-19.1:7.md"},r=i('<h1 id="§-30-19-1-7-bills-related-to-the-virginia-retirement-system-impact-statements" tabindex="-1">§ 30-19.1:7 Bills related to the Virginia Retirement System; impact statements. <a class="header-anchor" href="#§-30-19-1-7-bills-related-to-the-virginia-retirement-system-impact-statements" aria-label="Permalink to &quot;§ 30-19.1:7 Bills related to the Virginia Retirement System; impact statements.&quot;">​</a></h1><p>In accordance with a joint resolution that establishes a schedule for the conduct of business coming before a regular session of the General Assembly, the Board of Trustees of the Virginia Retirement System shall investigate, prepare, and submit to the Clerk of the House of Delegates, the Clerk of the Senate, the Commission on Local Government, the House Committee on Appropriations, and the Senate Committee on Finance and Appropriations, a statement of (i) the financial impact of the proposed bill upon the general fund and the various local governments that have elected to become part of the Virginia Retirement System pursuant to § <a href="/vacode/51.1-800/">51.1-800</a> and (ii) the policy implications that such bill will have on the various systems administered by the Board of Trustees. Such statement shall also note the potential impact any introduced bill will have on local government independent retirement systems.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0610" target="_blank" rel="noreferrer">610</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0680" target="_blank" rel="noreferrer">680</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>.</p>',3),o=[r];function s(l,c,h,m,p,d){return t(),a("div",null,o)}const g=e(n,[["render",s]]);export{_ as __pageData,g as default};
