import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 30-192.2 Quorum; meetings; voting on recommendations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-192.2.md","filePath":"vacode/30-192.2.md"}'),n={name:"vacode/30-192.2.md"},i=a('<h1 id="§-30-192-2-quorum-meetings-voting-on-recommendations" tabindex="-1">§ 30-192.2 Quorum; meetings; voting on recommendations. <a class="header-anchor" href="#§-30-192-2-quorum-meetings-voting-on-recommendations" aria-label="Permalink to &quot;§ 30-192.2 Quorum; meetings; voting on recommendations.&quot;">​</a></h1><p>A majority of the members shall constitute a quorum. The Commission shall meet no more than four times each year. The meetings of the Commission shall be held at the call of the chairman or whenever the majority of the members so request.</p><p>No recommendation of the Commission shall be adopted if a majority of the Senate members or a majority of the House members appointed to the Commission (i) vote against the recommendation and (ii) vote for the recommendation to fail notwithstanding the majority vote of the Commission.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1035" target="_blank" rel="noreferrer">1035</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1037" target="_blank" rel="noreferrer">1037</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>.</p>',4),r=[i];function m(s,h,c,l,d,_){return o(),t("div",null,r)}const p=e(n,[["render",m]]);export{g as __pageData,p as default};
