import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-192.3 Compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-192.3.md","filePath":"vacode/30-192.3.md"}'),i={name:"vacode/30-192.3.md"},o=n('<h1 id="§-30-192-3-compensation-expenses" tabindex="-1">§ 30-192.3 Compensation; expenses. <a class="header-anchor" href="#§-30-192-3-compensation-expenses" aria-label="Permalink to &quot;§ 30-192.3 Compensation; expenses.&quot;">​</a></h1><p>Legislative members of the Commission shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative citizen members shall receive such compensation for the performance of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a>. All members shall be reimbursed for reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. However, all such compensation and expenses shall be paid from existing appropriations to the Commission, or if unfunded, shall be approved by the Joint Rules Committee.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1035" target="_blank" rel="noreferrer">1035</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1037" target="_blank" rel="noreferrer">1037</a>.</p>',3),t=[o];function s(l,p,c,d,h,_){return a(),r("div",null,t)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
