import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-205 (Expires July 1, 2024) Powers and duties of the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-205.md","filePath":"vacode/30-205.md"}'),s={name:"vacode/30-205.md"},r=t('<h1 id="§-30-205-expires-july-1-2024-powers-and-duties-of-the-commission" tabindex="-1">§ 30-205 (Expires July 1, 2024) Powers and duties of the Commission. <a class="header-anchor" href="#§-30-205-expires-july-1-2024-powers-and-duties-of-the-commission" aria-label="Permalink to &quot;§ 30-205 (Expires July 1, 2024) Powers and duties of the Commission.&quot;">​</a></h1><p>The Commission shall have the following powers and duties:</p><ol><li><p>Monitor the work of the State Corporation Commission in implementing Chapter 23 (§ <a href="/vacode/56-576/">56-576</a> et seq.) of Title 56, receiving such reports as the Commission may be required to make pursuant thereto, including reviews, analyses, and impact on consumers of electric utility regulation in other states;</p></li><li><p>Examine generation, transmission and distribution systems reliability concerns;</p></li><li><p>Establish one or more subcommittees, composed of its membership, persons with expertise in the matters under consideration by the Commission, or both, to meet at the direction of the chairman of the Commission, for any purpose within the scope of the duties prescribed to the Commission by this section, provided that such persons who are not members of the Commission shall serve without compensation but shall be entitled to be reimbursed from funds appropriated or otherwise available to the Commission for reasonable and necessary expenses incurred in the performance of their duties; and</p></li><li><p>Report annually to the General Assembly and the Governor with such recommendations as may be appropriate for legislative and administrative consideration in order to maintain reliable service in the Commonwealth while preserving the Commonwealth&#39;s position as a low-cost electricity market.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0885" target="_blank" rel="noreferrer">885</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0812" target="_blank" rel="noreferrer">812</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0883" target="_blank" rel="noreferrer">883</a>.</p>',4),a=[r];function n(l,m,h,p,c,d){return i(),o("div",null,a)}const _=e(s,[["render",n]]);export{f as __pageData,_ as default};
