import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 30-231.7 Compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/30-231.7.md","filePath":"vacode/30-231.7.md"}'),n={name:"vacode/30-231.7.md"},i=t('<h1 id="§-30-231-7-compensation-expenses" tabindex="-1">§ 30-231.7 Compensation; expenses. <a class="header-anchor" href="#§-30-231-7-compensation-expenses" aria-label="Permalink to &quot;§ 30-231.7 Compensation; expenses.&quot;">​</a></h1><p>Legislative members of the Committee shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative citizen members shall receive such compensation for the performance of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a>. All members shall be reimbursed for reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. However, all such compensation and expenses shall be paid from existing appropriations to the Committee, or if unfunded, shall be approved by the Joint Rules Committee.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0753" target="_blank" rel="noreferrer">753</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>.</p>',3),o=[i];function s(l,p,c,d,h,_){return a(),r("div",null,o)}const v=e(n,[["render",s]]);export{f as __pageData,v as default};
